mapper.select:
select

from Insurance
where status != 'del'

  and id = #{id,jdbcType=BIGINT}


  and certificateCode = #{certificateCode,jdbcType=VARCHAR}


  and telBrand = #{telBrand,jdbcType=VARCHAR}


  and telModel = #{telModel,jdbcType=VARCHAR}


  and telIMEI = #{telIMEI,jdbcType=VARCHAR}


  and card_num = #{card_num,jdbcType=VARCHAR}


  and customerName like CONCAT('%',#{customerName},'%')


  and customerEmail like CONCAT('%',#{customerEmail},'%')


  and mobileNumber like CONCAT('%',#{mobileNumber},'%')


  and store = #{store,jdbcType=VARCHAR}


  and storeTransactor like CONCAT('%',#{storeTransactor},'%')


  and createTime = #{createTime,jdbcType=TIMESTAMP}


  and status = #{status,jdbcType=VARCHAR}


  and mark like CONCAT('%',#{mark},'%')
